mapper.update:
UPDATE parent_wallet 
SET balance = #{balance}, update_time = NOW()
WHERE parent_id = #{parentId}

UPDATE parent_wallet 
SET balance = balance + #{amount}, update_time = NOW()
WHERE parent_id = #{parentId}

UPDATE parent_wallet 
SET balance = balance - #{amount}, update_time = NOW()
WHERE parent_id = #{parentId} AND balance >= #{amount}
