import TabsContainer from '@/components/TabsContainer.vue';
export default {
  name: 'CourseFeedback',
  components: {
    TabsContainer
  },
  data() {
    return {
      loading: false,
      activeTab: 'unfilled',
      tabs: [{
        key: 'unfilled',
        label: '未填写课程反馈'
      }, {
        key: 'filled',
        label: '已填写课程反馈'
      }],
      searchForm: {
        courseName: '',
        subject: '',
        lessonName: '',
        teacher: ''
      },
      unfilledList: [
        // 模拟数据 - 未填写反馈
      ],
      filledList: [
        // 模拟数据 - 已填写反馈
      ]
    };
  },
  created() {
    this.loadData();
  },
  methods: {
    handleTabChange(tab) {
      this.activeTab = tab.key;
      this.loadData();
    },
    handleSearch() {
      this.loadData();
    },
    async loadData() {
      this.loading = true;
      try {
        // 模拟API调用
        await new Promise(resolve => setTimeout(resolve, 500));
        if (this.activeTab === 'unfilled') {
          this.unfilledList = [];
        } else {
          this.filledList = [];
        }
      } catch (error) {
        this.$message.error('加载数据失败');
      } finally {
        this.loading = false;
      }
    },
    getStatusText(status) {
      const statusMap = {
        'completed': '已完成',
        'reviewed': '已审核',
        'pending': '待处理'
      };
      return statusMap[status] || '未知';
    },
    getStatusClass(status) {
      const classMap = {
        'completed': 'active',
        'reviewed': 'active',
        'pending': 'pending'
      };
      return classMap[status] || 'default';
    }
  }
};
