import request from './request';

/**
 * 课程相关API
 */
export default {
  // ========== 基础CRUD操作 ==========

  /**
   * 创建课程
   */
  createCourse(data) {
    return request({
      url: '/course',
      method: 'post',
      data
    });
  },
  /**
   * 删除课程
   */
  deleteCourse(id) {
    return request({
      url: `/course/${id}`,
      method: 'delete'
    });
  },
  /**
   * 更新课程
   */
  updateCourse(id, data) {
    return request({
      url: `/course/${id}`,
      method: 'put',
      data
    });
  },
  /**
   * 根据ID获取课程
   */
  getCourseById(id) {
    return request({
      url: `/course/${id}`,
      method: 'get'
    });
  },
  /**
   * 根据课程编号获取课程
   */
  getCourseByCourseNo(courseNo) {
    return request({
      url: `/course/no/${courseNo}`,
      method: 'get'
    });
  },
  /**
   * 获取所有课程
   */
  getAllCourses() {
    return request({
      url: '/course',
      method: 'get'
    });
  },
  /**
   * 分页获取课程
   */
  getCoursesByPage(page = 1, size = 10) {
    return request({
      url: '/course/page',
      method: 'get',
      params: {
        page,
        size
      }
    });
  },
  /**
   * 根据条件查询课程
   */
  getCoursesByCondition(params) {
    return request({
      url: '/course/search',
      method: 'get',
      params
    });
  },
  // ========== 分类和教师相关查询 ==========

  /**
   * 根据教师ID查询课程
   */
  getCoursesByTeacherId(teacherId) {
    return request({
      url: `/course/teacher/${teacherId}`,
      method: 'get'
    });
  },
  /**
   * 根据分类ID查询课程
   */
  getCoursesByCategoryId(categoryId) {
    return request({
      url: `/course/category/${categoryId}`,
      method: 'get'
    });
  },
  // ========== 状态管理 ==========

  /**
   * 获取待审核课程
   */
  getPendingReviewCourses() {
    return request({
      url: '/course/pending-review',
      method: 'get'
    });
  },
  /**
   * 获取已发布课程
   */
  getPublishedCourses() {
    return request({
      url: '/course/published',
      method: 'get'
    });
  },
  /**
   * 审核课程
   */
  reviewCourse(id, reviewStatus, reviewReason, reviewerId) {
    return request({
      url: `/course/${id}/review`,
      method: 'put',
      params: {
        reviewStatus,
        reviewReason,
        reviewerId
      }
    });
  },
  /**
   * 发布/下架课程
   */
  publishCourse(id, publishStatus) {
    return request({
      url: `/course/${id}/publish`,
      method: 'put',
      params: {
        publishStatus
      }
    });
  },
  /**
   * 更新课程状态
   */
  updateCourseStatus(id, status) {
    return request({
      url: `/course/${id}/status`,
      method: 'put',
      params: {
        status
      }
    });
  },
  // ========== 批量操作 ==========

  /**
   * 批量审核课程
   */
  batchReviewCourses(data) {
    return request({
      url: '/course/batch/review',
      method: 'put',
      data
    });
  },
  /**
   * 批量发布课程
   */
  batchPublishCourses(data) {
    return request({
      url: '/course/batch/publish',
      method: 'put',
      data
    });
  },
  /**
   * 批量删除课程
   */
  batchDeleteCourses(ids) {
    return request({
      url: '/course/batch',
      method: 'delete',
      data: ids
    });
  },
  // ========== 特殊类型课程 ==========

  /**
   * 获取热门课程
   */
  getPopularCourses(limit = 10) {
    return request({
      url: '/course/popular',
      method: 'get',
      params: {
        limit
      }
    });
  },
  /**
   * 获取最新课程
   */
  getLatestCourses(limit = 10) {
    return request({
      url: '/course/latest',
      method: 'get',
      params: {
        limit
      }
    });
  },
  /**
   * 获取免费课程
   */
  getFreeCourses() {
    return request({
      url: '/course/free',
      method: 'get'
    });
  },
  /**
   * 获取直播课程
   */
  getLiveCourses() {
    return request({
      url: '/course/live',
      method: 'get'
    });
  },
  /**
   * 获取即将开始的直播课程
   */
  getUpcomingLiveCourses(startTime, endTime) {
    return request({
      url: '/course/live/upcoming',
      method: 'get',
      params: {
        startTime,
        endTime
      }
    });
  },
  /**
   * 获取正在进行的直播课程
   */
  getOngoingLiveCourses() {
    return request({
      url: '/course/live/ongoing',
      method: 'get'
    });
  },
  // ========== 统计数据 ==========

  /**
   * 更新课程统计数据
   */
  updateCourseStats(id, params) {
    return request({
      url: `/course/${id}/stats`,
      method: 'put',
      params
    });
  },
  /**
   * 增加浏览次数
   */
  incrementViewCount(id) {
    return request({
      url: `/course/${id}/view`,
      method: 'put'
    });
  },
  /**
   * 增加购买次数
   */
  incrementPurchaseCount(id) {
    return request({
      url: `/course/${id}/purchase`,
      method: 'put'
    });
  },
  /**
   * 更新学生数量
   */
  updateStudentCount(id, currentStudents) {
    return request({
      url: `/course/${id}/students`,
      method: 'put',
      params: {
        currentStudents
      }
    });
  },
  /**
   * 更新课程价格
   */
  updateCoursePrice(id, originalPrice, currentPrice) {
    return request({
      url: `/course/${id}/price`,
      method: 'put',
      params: {
        originalPrice,
        currentPrice
      }
    });
  },
  // ========== 搜索和工具 ==========

  /**
   * 搜索课程
   */
  searchCourses(keyword) {
    return request({
      url: '/course/search/keyword',
      method: 'get',
      params: {
        keyword
      }
    });
  },
  /**
   * 生成课程编号
   */
  generateCourseNo() {
    return request({
      url: '/course/generate-no',
      method: 'get'
    });
  },
  /**
   * 检查课程编号是否存在
   */
  checkCourseNo(courseNo) {
    return request({
      url: '/course/check-no',
      method: 'get',
      params: {
        courseNo
      }
    });
  },
  /**
   * 根据标签查询课程
   */
  getCoursesByTags(tags) {
    return request({
      url: '/course/tags',
      method: 'get',
      params: {
        tags
      }
    });
  },
  /**
   * 更新课程材料
   */
  updateCourseMaterials(id, courseMaterials) {
    return request({
      url: `/course/${id}/materials`,
      method: 'put',
      params: {
        courseMaterials
      }
    });
  },
  // ========== 统计分析 ==========

  /**
   * 课程统计 - 按分类统计
   */
  getStatsByCategory() {
    return request({
      url: '/course/stats/category',
      method: 'get'
    });
  },
  /**
   * 课程统计 - 按教师统计
   */
  getStatsByTeacher() {
    return request({
      url: '/course/stats/teacher',
      method: 'get'
    });
  },
  /**
   * 课程统计 - 按难度统计
   */
  getStatsByDifficulty() {
    return request({
      url: '/course/stats/difficulty',
      method: 'get'
    });
  },
  /**
   * 课程统计 - 按状态统计
   */
  getStatsByStatus() {
    return request({
      url: '/course/stats/status',
      method: 'get'
    });
  },
  /**
   * 课程统计 - 按类型统计
   */
  getStatsByType() {
    return request({
      url: '/course/stats/type',
      method: 'get'
    });
  },
  /**
   * 获取总体统计数据
   */
  getOverallStats() {
    return request({
      url: '/course/stats/overall',
      method: 'get'
    });
  },
  /**
   * 获取收入统计
   */
  getRevenueStats(startDate, endDate) {
    return request({
      url: '/course/stats/revenue',
      method: 'get',
      params: {
        startDate,
        endDate
      }
    });
  },
  /**
   * 按时间范围获取统计数据
   */
  getTimeRangeStats(startDate, endDate, groupBy) {
    return request({
      url: '/course/stats/time-range',
      method: 'get',
      params: {
        startDate,
        endDate,
        groupBy
      }
    });
  },
  // ========== 推荐和导出 ==========

  /**
   * 获取推荐课程
   */
  getRecommendedCourses(userId, limit = 10) {
    return request({
      url: '/course/recommend',
      method: 'get',
      params: {
        userId,
        limit
      }
    });
  },
  /**
   * 导出课程数据
   */
  exportCourseData(params) {
    return request({
      url: '/course/export',
      method: 'post',
      data: params
    });
  },
  /**
   * 生成课程统计报表
   */
  generateCourseReport(startDate, endDate) {
    return request({
      url: '/course/report',
      method: 'get',
      params: {
        startDate,
        endDate
      }
    });
  }
};
