import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/login'
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('../views/Login.vue'),
    meta: { title: '登录' }
  },
  {
    path: '/dashboard',
    name: 'Dashboard',
    component: () => import('../views/Dashboard.vue'),
    meta: { title: '管理仪表盘', requiresAuth: true }
  },

  {
    path: '/teachers',
    name: 'Teachers',
    component: () => import('../views/education/Teachers.vue'),
    meta: { title: '教师管理', requiresAuth: true }
  },
  {
    path: '/teacher-review',
    name: 'TeacherReview',
    component: () => import('../views/education/TeacherReview.vue'),
    meta: { title: '教师审核', requiresAuth: true }
  },
  {
    path: '/students',
    name: 'Students',
    component: () => import('../views/education/Students.vue'),
    meta: { title: '学生管理', requiresAuth: true }
  },
  {
    path: '/courses',
    name: 'Courses',
    component: () => import('../views/education/Courses.vue'),
    meta: { title: '课程管理', requiresAuth: true }
  },
  {
    path: '/course-categories',
    name: 'CourseCategories',
    component: () => import('../views/education/CourseCategories.vue'),
    meta: { title: '课程分类管理', requiresAuth: true }
  },
  {
    path: '/orders',
    name: 'Orders',
    component: () => import('../views/finance/Orders.vue'),
    meta: { title: '订单管理', requiresAuth: true }
  },
  {
    path: '/complaints',
    name: 'Complaints',
    component: () => import('../views/education/Complaints.vue'),
    meta: { title: '投诉管理', requiresAuth: true }
  },
  {
    path: '/operation',
    name: 'Operation',
    component: () => import('../views/operation/Operation.vue'),
    meta: { title: '运营中心', requiresAuth: true }
  },
  {
    path: '/finance',
    name: 'Finance',
    component: () => import('../views/finance/Finance.vue'),
    meta: { title: '财务管理', requiresAuth: true }
  },
  {
    path: '/content',
    name: 'Content',
    component: () => import('../views/content/Content.vue'),
    meta: { title: '内容管理', requiresAuth: true }
  },
  {
    path: '/404',
    name: 'NotFound',
    component: () => import('../views/404.vue'),
    meta: { title: '页面不存在' }
  },
  {
    path: '*',
    redirect: '/404'
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

// 路由守卫
router.beforeEach((to, from, next) => {
  // 设置页面标题
  if (to.meta.title) {
    document.title = to.meta.title + ' - 教育管理系统'
  }

  // 检查是否需要登录
  if (to.meta.requiresAuth) {
    const token = localStorage.getItem('token')
    if (!token) {
      next('/login')
      return
    }
  }

  next()
})

export default router
