export default {
  name: 'Statistics',
  data() {
    return {
      loading: false,
      showDetailDialog: false,
      currentTeacher: null,
      // 筛选表单
      filterForm: {
        teacherName: '',
        subject: '',
        dateRange: []
      },
      // 分页数据
      pagination: {
        currentPage: 1,
        pageSize: 20,
        total: 0
      },
      // 概览数据
      overviewData: {
        totalTeachers: 126,
        totalHours: 15680,
        avgRating: 4.3,
        completionRate: 94
      },
      // 表格数据
      tableData: [{
        id: 1,
        teacherName: 'George Li',
        subject: '数学',
        totalHours: 160,
        studentCount: 25,
        avgRating: 4.8,
        completionRate: 96,
        onTimeRate: 98,
        satisfaction: 'high',
        monthlyIncome: 12000,
        workYears: 5
      }, {
        id: 2,
        teacherName: '王老师',
        subject: '物理',
        totalHours: 145,
        studentCount: 22,
        avgRating: 4.5,
        completionRate: 94,
        onTimeRate: 95,
        satisfaction: 'high',
        monthlyIncome: 10500,
        workYears: 3
      }, {
        id: 3,
        teacherName: '李老师',
        subject: '化学',
        totalHours: 138,
        studentCount: 20,
        avgRating: 4.2,
        completionRate: 89,
        onTimeRate: 92,
        satisfaction: 'medium',
        monthlyIncome: 9800,
        workYears: 4
      }]
    };
  },
  mounted() {
    this.loadData();
  },
  methods: {
    // 加载数据
    loadData() {
      this.loading = true;
      // 模拟API调用
      setTimeout(() => {
        this.pagination.total = this.tableData.length;
        this.loading = false;
      }, 500);
    },
    // 搜索
    handleSearch() {
      console.log('搜索参数:', this.filterForm);
      this.loadData();
    },
    // 重置
    handleReset() {
      this.filterForm = {
        teacherName: '',
        subject: '',
        dateRange: []
      };
      this.loadData();
    },
    // 分页
    handleSizeChange(val) {
      this.pagination.pageSize = val;
      this.loadData();
    },
    handleCurrentChange(val) {
      this.pagination.currentPage = val;
      this.loadData();
    },
    // 获取进度条颜色
    getProgressColor(percentage) {
      if (percentage >= 90) return '#67c23a';
      if (percentage >= 70) return '#e6a23c';
      return '#f56c6c';
    },
    // 获取满意度类型
    getSatisfactionType(satisfaction) {
      const typeMap = {
        high: 'success',
        medium: 'warning',
        low: 'danger'
      };
      return typeMap[satisfaction] || 'info';
    },
    // 获取满意度文本
    getSatisfactionText(satisfaction) {
      const textMap = {
        high: '很满意',
        medium: '一般',
        low: '待改进'
      };
      return textMap[satisfaction] || '未知';
    },
    // 操作方法
    handleViewDetail(row) {
      this.currentTeacher = row;
      this.showDetailDialog = true;
    },
    handleGenerateReport(row) {
      this.$message.success(`正在为${row.teacherName}生成报告...`);
    },
    handleExport() {
      this.$message.success('正在导出统计报表...');
    }
  }
};
