import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    // 用户信息
    user: null,
    // 登录状态
    isLoggedIn: false,
    // token
    token: localStorage.getItem('token') || null,
    // 侧边栏折叠状态
    sidebarCollapsed: false,
    // 标签页列表
    tabs: [],
    // 当前激活的标签页
    activeTabKey: ''
  },
  getters: {
    // 获取用户信息
    getUser: state => state.user,
    // 获取登录状态
    isLoggedIn: state => state.isLoggedIn,
    // 获取token
    getToken: state => state.token,
    // 获取侧边栏状态
    getSidebarCollapsed: state => state.sidebarCollapsed,
    // 获取标签页列表
    getTabs: state => state.tabs,
    // 获取当前激活的标签页
    getActiveTabKey: state => state.activeTabKey
  },
  mutations: {
    // 设置用户信息
    SET_USER(state, user) {
      state.user = user
      state.isLoggedIn = !!user
    },
    // 设置token
    SET_TOKEN(state, token) {
      state.token = token
      if (token) {
        localStorage.setItem('token', token)
      } else {
        localStorage.removeItem('token')
      }
    },
    // 清除用户信息
    CLEAR_USER(state) {
      state.user = null
      state.isLoggedIn = false
      state.token = null
      localStorage.removeItem('token')
    },
    // 切换侧边栏状态
    TOGGLE_SIDEBAR(state) {
      state.sidebarCollapsed = !state.sidebarCollapsed
    },
    // 添加标签页
    ADD_TAB(state, { key, title }) {
      // 检查是否已存在
      const existingTab = state.tabs.find(tab => tab.key === key)
      if (!existingTab) {
        state.tabs.push({ key, title })
      }
      state.activeTabKey = key
    },
    // 移除标签页
    REMOVE_TAB(state, targetKey) {
      const tabs = state.tabs
      const activeKey = state.activeTabKey
      
      if (activeKey === targetKey) {
        tabs.forEach((tab, index) => {
          if (tab.key === targetKey) {
            const nextTab = tabs[index + 1] || tabs[index - 1]
            if (nextTab) {
              state.activeTabKey = nextTab.key
            } else {
              state.activeTabKey = ''
            }
          }
        })
      }
      
      state.tabs = tabs.filter(tab => tab.key !== targetKey)
    },
    // 设置当前激活的标签页
    SET_ACTIVE_TAB(state, key) {
      state.activeTabKey = key
    }
  },
  actions: {
    // 登录
    login({ commit }, { user, token }) {
      commit('SET_USER', user)
      commit('SET_TOKEN', token)
    },
    // 登出
    logout({ commit }) {
      commit('CLEAR_USER')
    },
    // 切换侧边栏
    toggleSidebar({ commit }) {
      commit('TOGGLE_SIDEBAR')
    }
  },
  modules: {
  }
})
