import request from './request';

/**
 * 课程分类相关API
 */
export default {
  // ========== 基础CRUD操作 ==========

  /**
   * 创建分类
   */
  createCategory(data) {
    return request({
      url: '/category',
      method: 'post',
      data
    });
  },
  /**
   * 删除分类
   */
  deleteCategory(id) {
    return request({
      url: `/category/${id}`,
      method: 'delete'
    });
  },
  /**
   * 更新分类
   */
  updateCategory(id, data) {
    return request({
      url: `/category/${id}`,
      method: 'put',
      data
    });
  },
  /**
   * 根据ID获取分类
   */
  getCategoryById(id) {
    return request({
      url: `/category/${id}`,
      method: 'get'
    });
  },
  /**
   * 获取所有分类
   */
  getAllCategories() {
    return request({
      url: '/category',
      method: 'get'
    });
  },
  // ========== 树形结构和层级 ==========

  /**
   * 获取分类树形结构
   */
  getCategoryTree() {
    return request({
      url: '/category/tree',
      method: 'get'
    });
  },
  /**
   * 根据父分类ID获取子分类
   */
  getCategoriesByParentId(parentId) {
    return request({
      url: `/category/parent/${parentId}`,
      method: 'get'
    });
  },
  /**
   * 根据等级获取分类
   */
  getCategoriesByLevel(level) {
    return request({
      url: `/category/level/${level}`,
      method: 'get'
    });
  },
  // ========== 分页和查询 ==========

  /**
   * 分页获取分类
   */
  getCategoriesByPage(page = 1, size = 10) {
    return request({
      url: '/category/page',
      method: 'get',
      params: {
        page,
        size
      }
    });
  },
  /**
   * 根据条件查询分类
   */
  getCategoriesByCondition(categoryName, parentId, level, status) {
    return request({
      url: '/category/search',
      method: 'get',
      params: {
        categoryName,
        parentId,
        level,
        status
      }
    });
  },
  /**
   * 搜索分类
   */
  searchCategories(keyword) {
    return request({
      url: '/category/search/keyword',
      method: 'get',
      params: {
        keyword
      }
    });
  },
  // ========== 状态管理 ==========

  /**
   * 更新分类状态
   */
  updateCategoryStatus(id, status) {
    return request({
      url: `/category/${id}/status`,
      method: 'put',
      params: {
        status
      }
    });
  },
  // ========== 排序管理 ==========

  /**
   * 更新排序
   */
  updateSortOrder(id, sortOrder) {
    return request({
      url: `/category/${id}/sort`,
      method: 'put',
      params: {
        sortOrder
      }
    });
  },
  /**
   * 批量更新排序
   */
  batchUpdateSortOrder(categories) {
    return request({
      url: '/category/batch/sort',
      method: 'put',
      data: categories
    });
  },
  /**
   * 移动分类位置
   */
  moveCategoryPosition(id, newSortOrder) {
    return request({
      url: `/category/${id}/move`,
      method: 'put',
      params: {
        newSortOrder
      }
    });
  },
  /**
   * 获取下一个排序值
   */
  getNextSortOrder(parentId = 0) {
    return request({
      url: '/category/next-sort-order',
      method: 'get',
      params: {
        parentId
      }
    });
  },
  // ========== 统计和关联 ==========

  /**
   * 获取分类下的课程数量
   */
  getCourseCount(id) {
    return request({
      url: `/category/${id}/course-count`,
      method: 'get'
    });
  },
  // ========== 批量操作 ==========

  /**
   * 批量删除分类
   */
  batchDeleteCategories(ids) {
    return request({
      url: '/category/batch',
      method: 'delete',
      data: ids
    });
  },
  // ========== 验证工具 ==========

  /**
   * 检查分类名称是否存在
   */
  checkCategoryName(categoryName, parentId = 0) {
    return request({
      url: '/category/check-name',
      method: 'get',
      params: {
        categoryName,
        parentId
      }
    });
  }
};
