import TabsContainer from '@/components/TabsContainer.vue';
import CircleButton from '@/components/CircleButton.vue';
export default {
  name: 'VideoClips',
  components: {
    TabsContainer,
    CircleButton
  },
  data() {
    return {
      loading: false,
      activeTab: 'unclipped',
      tabs: [{
        key: 'unclipped',
        label: '未打点视频'
      }, {
        key: 'clipped',
        label: '已打点视频'
      }],
      searchForm: {
        courseName: '',
        subject: '',
        teacher: ''
      },
      unclippedList: [],
      clippedList: []
    };
  },
  created() {
    this.loadData();
  },
  methods: {
    handleTabChange(tab) {
      this.activeTab = tab.key;
      this.loadData();
    },
    handleSearch() {
      this.loadData();
    },
    handleAction(actionData) {
      const {
        action,
        data
      } = actionData;
      switch (action) {
        case 'start-clip':
          this.startClipping(data);
          break;
        case 'view-clips':
          this.viewClips(data);
          break;
        case 'edit-clips':
          this.editClips(data);
          break;
        default:
          console.log('未知操作:', action);
      }
    },
    startClipping(video) {
      this.$message.info(`开始为视频 "${video.courseName}" 打点`);
    },
    viewClips(video) {
      this.$message.info(`查看视频 "${video.courseName}" 的打点`);
    },
    editClips(video) {
      this.$message.info(`编辑视频 "${video.courseName}" 的打点`);
    },
    async loadData() {
      this.loading = true;
      try {
        // 模拟API调用
        await new Promise(resolve => setTimeout(resolve, 500));
        if (this.activeTab === 'unclipped') {
          this.unclippedList = [];
        } else {
          this.clippedList = [];
        }
      } catch (error) {
        this.$message.error('加载数据失败');
      } finally {
        this.loading = false;
      }
    }
  }
};
