/**
 * 根据路径查找菜单信息
 * @param {string} path - 路由路径
 * @returns {Object} 菜单信息
 */
export function findMenuByPath(path) {
  for (const menu of MAIN_MENUS) {
    for (const child of menu.children) {
      if (child.path === path) {
        return {
          parent: {
            id: menu.id,
            name: menu.name
          },
          current: {
            id: child.id,
            name: child.name
          }
        };
      }
    }
  }
  return null;
}

/**
 * 获取一级菜单列表
 * @returns {Array} 一级菜单列表
 */
export function getMainMenus() {
  return MAIN_MENUS.map(menu => ({
    id: menu.id,
    name: menu.name,
    icon: menu.icon,
    path: menu.path || null
  }));
}

/**
 * 根据菜单ID获取子菜单列表
 * @param {string} menuId - 菜单ID
 * @returns {Array} 子菜单列表
 */
export function getSubMenus(menuId) {
  const menu = MAIN_MENUS.find(m => m.id === menuId);
  return menu ? menu.children : [];
}

/**
 * 获取面包屑导航数据
 * @param {string} path - 当前路径
 * @returns {Object} 面包屑数据
 */
export function getBreadcrumbData(path) {
  const menuInfo = findMenuByPath(path);
  if (!menuInfo) {
    return {
      items: [{
        name: '首页',
        path: '/dashboard'
      }]
    };
  }
  const items = [{
    name: '首页',
    path: '/dashboard'
  }];
  if (menuInfo.parent) {
    items.push({
      name: menuInfo.parent.name,
      path: ''
    });
  }
  if (menuInfo.current) {
    items.push({
      name: menuInfo.current.name,
      path: path
    });
  }
  return {
    items
  };
}
