export default {
  name: 'Layout',
  computed: {
    userInfo() {
      return this.$store.getters.getUser || {
        username: 'Admin',
        nickname: '管理员'
      };
    },
    sidebarCollapsed() {
      return this.$store.getters.getSidebarCollapsed;
    },
    sidebarWidth() {
      return this.sidebarCollapsed ? '64px' : '220px';
    },
    activeMenu() {
      const path = this.$route.path;
      // 处理子菜单的激活状态
      if (path.includes('/teacher')) return '/teachers';
      if (path.includes('/order') || path.includes('/finance')) return '/orders';
      return path;
    }
  },
  methods: {
    toggleSidebar() {
      this.$store.dispatch('toggleSidebar');
    },
    handleCommand(command) {
      switch (command) {
        case 'profile':
          this.$message.info('个人设置功能开发中...');
          break;
        case 'logout':
          this.handleLogout();
          break;
      }
    },
    handleLogout() {
      this.$confirm('确定要退出登录吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.$store.dispatch('logout');
        this.$message.success('退出登录成功');
        this.$router.push('/login');
      }).catch(() => {
        // 取消操作
      });
    }
  }
};
