import Vue from 'vue'
import VueRouter from 'vue-router'
import HorizontalLayout from '@/components/HorizontalLayout.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/dashboard'
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('../views/Login.vue'),
    meta: { title: '登录', hideLayout: true }
  },
  // 主要管理界面路由，使用水平布局
  {
    path: '/',
    component: HorizontalLayout,
    children: [
      // 数据研究模块
      {
        path: '/dashboard',
        name: 'Dashboard',
        component: () => import('../views/Dashboard.vue'),
        meta: { title: '数学教研', module: 'data-research', requiresAuth: true }
      },

      // 人事行政模块
      {
        path: '/teachers',
        name: 'Teachers',
        component: () => import('../views/education/Teachers.vue'),
        meta: { title: '教师列表', module: 'hr-admin', requiresAuth: true }
      },
      {
        path: '/teacher-review',
        name: 'TeacherReview',
        component: () => import('../views/education/TeacherReview.vue'),
        meta: { title: '教师审核', module: 'hr-admin', requiresAuth: true }
      },

      // 薪火计划模块
      {
        path: '/students',
        name: 'Students',
        component: () => import('../views/education/Students.vue'),
        meta: { title: '一对一学员', module: 'xinghuo-plan', requiresAuth: true }
      },
      {
        path: '/students/one-to-one',
        name: 'CourseFeedback',
        component: () => import('../views/education/CourseFeedback.vue'),
        meta: { title: '一对一课程反馈', module: 'xinghuo-plan', requiresAuth: true }
      },

      // 考试管理模块
      {
        path: '/exams',
        name: 'Exams',
        component: () => import('../views/education/Exams.vue'),
        meta: { title: '考试管理', module: 'exam-management', requiresAuth: true }
      },

      // 竞赛课程模块
      {
        path: '/courses',
        name: 'Courses',
        component: () => import('../views/education/Courses.vue'),
        meta: { title: '课程列表', module: 'competition-courses', requiresAuth: true }
      },
      {
        path: '/course-categories',
        name: 'CourseCategories',
        component: () => import('../views/education/CourseCategories.vue'),
        meta: { title: '课程分类', module: 'competition-courses', requiresAuth: true }
      },
      {
        path: '/video-clips',
        name: 'VideoClips',
        component: () => import('../views/education/VideoClips.vue'),
        meta: { title: '视频打点', module: 'competition-courses', requiresAuth: true }
      },

      // 系统管理模块
      {
        path: '/system/leave-request',
        name: 'LeaveRequest',
        component: () => import('../views/system/LeaveRequest.vue'),
        meta: { title: '网课模考评请', module: 'system-management', requiresAuth: true }
      },
      {
        path: '/system/questionnaire',
        name: 'Questionnaire',
        component: () => import('../views/system/Questionnaire.vue'),
        meta: { title: '问卷调查管理', module: 'system-management', requiresAuth: true }
      },
      {
        path: '/system/statistics',
        name: 'TeacherStatistics',
        component: () => import('../views/system/Statistics.vue'),
        meta: { title: '教师数据统计', module: 'system-management', requiresAuth: true }
      },

      // 财务管理模块
      {
        path: '/orders',
        name: 'Orders',
        component: () => import('../views/finance/Orders.vue'),
        meta: { title: '订单管理', module: 'finance-management', requiresAuth: true }
      },
      {
        path: '/finance',
        name: 'Finance',
        component: () => import('../views/finance/Finance.vue'),
        meta: { title: '财务数据', module: 'finance-management', requiresAuth: true }
      },

      // 运营管理模块
      {
        path: '/operation',
        name: 'Operation',
        component: () => import('../views/operation/Operation.vue'),
        meta: { title: '运营中心', module: 'operation-management', requiresAuth: true }
      },

      // 内容管理模块
      {
        path: '/content',
        name: 'Content',
        component: () => import('../views/content/Content.vue'),
        meta: { title: '内容审核', module: 'content-management', requiresAuth: true }
      },

      // 投诉管理模块
      {
        path: '/complaints',
        name: 'Complaints',
        component: () => import('../views/education/Complaints.vue'),
        meta: { title: '投诉处理', module: 'complaint-management', requiresAuth: true }
      }
    ]
  },
  {
    path: '/404',
    name: 'NotFound',
    component: () => import('../views/404.vue'),
    meta: { title: '页面不存在', hideLayout: true }
  },
  {
    path: '*',
    redirect: '/404'
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

// 路由守卫
router.beforeEach((to, from, next) => {
  // 设置页面标题
  if (to.meta.title) {
    document.title = to.meta.title + ' - 教育管理系统'
  }

  // 检查是否需要登录
  if (to.meta.requiresAuth) {
    const token = localStorage.getItem('token')
    if (!token) {
      next('/login')
      return
    }
  }

  next()
})

export default router
